import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import axios from "axios";
import CustomInput from "../components/CustomInput";
import { AuthContext } from "../context/AuthContext";

const CreateImage = () => {
  const [prompt, setPrompt] = useState("");
  const [passwordCheck, setPasswordCheck] = useState("");
  const [, setMe] = useContext(AuthContext);
  const navigate = useNavigate();

  const submitHandler = async (e) => {
    try {
      e.preventDefault();
      if (username.length < 3)
        throw new Error("Make the username longer than 3 characters");
      if (password.length < 6)
        throw new Error("Make the password longer than 6 characters");
      if (password !== passwordCheck)
        throw new Error("Password is different with Password Confirmation");
      const result = await axios.post("/users/register", {
        name,
        username,
        password,
      });
      console.log(result.data);
      setMe({
        userId: result.data.userId,
        sessionId: result.data.sessionId,
        name: result.data.name,
      });
      toast.success("Success!");
      navigate("/");
    } catch (err) {
      console.error(err);
      toast.error(err.message);
    }
  };

  return (
    <div
      style={{
        maxWidth: "100%",
        marginLeft: "auto",
        marginRight: "auto",
      }}
    >
      <form onSubmit={submitHandler}>
        <CustomInput label="Prompt" setValue={setPrompt} />
        <button
          style={{
            marginTop: 5,
          }}
          type="submit"
        >
          Create Image!
        </button>
      </form>
    </div>
  );
};

export default CreateImage;
