import React, { useState, useContext } from "react";
import CustomInput from "../components/CustomInput";
import axios from "axios";
import { toast } from "react-toastify";
import { AuthContext } from "../context/AuthContext";

const LoginPage = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [_, setMe] = useContext(AuthContext);

  const loginHandler = async (e) => {
    try {
      e.preventDefault();
      await axios.patch("/users/login", { username, password });
      setMe({ name: result.data.name, sessionId: result.data.sessionId });
      toast.success("Log In!");
    } catch (err) {
      console.error(err);
      toast.error(err.message);
    }
  };

  return (
    <div
      style={{
        marginTop: 100,
        maxWidth: 350,
        marginLeft: "auto",
        marginRight: "auto",
      }}
    >
      <h3>Log In</h3>
      <form onSubmit={loginHandler}>
        <CustomInput label="Username" value={username} setValue={setUsername} />
        <CustomInput
          label="Password"
          value={password}
          setValue={setPassword}
          type="password"
        />
        <button type="submit">Log In!</button>
      </form>
    </div>
  );
};

export default LoginPage;
