import React, { useState, useContext } from "react";
import axios from "axios";
import "./UploadForm.css";
import { toast } from "react-toastify";
import ProgressBar from "./ProgressBar";
import { ImageContext } from "../context/ImageContext";

const UploadForm = () => {
  const { images, setImages, myImages, setMyImages } = useContext(ImageContext);
  const defaultFileName = "Please upload image file";
  const [imageFiles, setImageFiles] = useState(null);

  const [imgSrc, setImgSrc] = useState(null);
  const [fileName, setFileName] = useState(defaultFileName);

  const [previews, setPreviews] = useState([]);

  const [percent, setPercent] = useState(0);
  const [isPublic, setIsPublic] = useState(true);

  const imageSelectHandler = async (event) => {
    const imageFileInfo = event.target.files;
    setImageFiles(imageFileInfo);

    const imagePreviews = await Promise.all(
      [...imageFileInfo].map(async (imgFile) => {
        return new Promise((resolve, reject) => {
          try {
            const fileReader = new FileReader();
            fileReader.readAsDataURL(imgFile);
            fileReader.onload = (e) =>
              resolve({ imgSrc: e.target.result, fileName: imgFile.name });
          } catch (err) {
            reject(err);
          }
        });
      })
    );
    setPreviews(imagePreviews);
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    for (let imageFile of imageFiles) {
      formData.append("imageTest", imageFile);
    }
    formData.append("public", isPublic);

    try {
      const res = await axios.post("/images", formData, {
        headers: { "Content-Type": "multipart/form-data" },
        onUploadProgress: (e) => {
          setPercent(Math.round((100 * e.loaded) / e.total));
        },
      });

      if (isPublic) setImages([...images, ...res.data]);
      else setMyImages([...myImages, ...res.data]);

      toast.success("success!");
      setTimeout(() => {
        setPercent(0);
        setFileName(defaultFileName);
        setPreviews([]);
      }, 3000);
    } catch (err) {
      toast.error(err.response.data.message);
      console.log({ err });
      setPercent(0);
      setFileName(defaultFileName);
      setPreviews([]);
      toast.error("fail!");
    }
  };

  const previewImages = previews.map((preview, index) => (
    <img
      key={index}
      style={{ width: 200, height: 200, objectFit: "cover" }}
      src={preview.imgSrc}
      alt=""
      className={`image-preview ${preview.imgSrc && "image-preview-show"}`}
    />
  ));

  return (
    <>
      <form onSubmit={onSubmit}>
        <div style={{ display: "flex", flexWrap: "wrap" }}>{previewImages}</div>
        <ProgressBar percent={percent} />
        <div className="file-dropper">
          {fileName}
          <input
            id="image"
            type="file"
            multiple={true}
            accept="image/*"
            onChange={imageSelectHandler}
          />
        </div>
        <input
          type="checkbox"
          id="public-check"
          value={!isPublic}
          onChange={() => setIsPublic(!isPublic)}
        />
        <label htmlFor="public-check">비공개</label>
        <button
          type="submit"
          style={{
            width: "100%",
            height: 40,
            borderRadius: 3,
            cursor: "pointer",
          }}
        >
          Submit
        </button>
      </form>
    </>
  );
};

export default UploadForm;
