import React, {
  useState,
  useEffect,
  createContext,
  useContext,
  useCallback,
} from "react";
import axios from "axios";
import { AuthContext } from "./AuthContext";

export const ImageContext = createContext();

export const ImageProvider = (prop) => {
  const [images, setImages] = useState([]);

  const [myImages, setMyImages] = useState([]);

  const [isPublic, setIsPublic] = useState(true);

  const [imageUrl, setImageUrl] = useState("/images");

  const [imageLoading, setImageLoading] = useState(false);

  const [imageError, setImageError] = useState(false);

  const [me] = useContext(AuthContext);

  useEffect(() => {
    setImageLoading(true);
    axios
      .get(imageUrl)
      .then((result) =>
        isPublic
          ? setImages((prevData) => [...prevData, ...result.data])
          : setMyImages((prevData) => [...prevData, ...result.data])
      )

      .catch((err) => {
        console.log(err);
        setImageError(err);
      })
      .finally(() => setImageLoading(false));
  }, [imageUrl]); // get public data

  useEffect(() => {
    if (me) {
      setTimeout(() => {
        axios
          .get("/users/me/images")
          .then((result) => setMyImages(result.data))
          .catch((err) => console.log(err));
      }, 0); // "To make it happen next time for the event look, Set Time Out!"
    } else {
      setMyImages([]);
      setIsPublic(true);
    }
  }, [me]);

  const loaderMoreImages = useCallback(() => {
    if (images.length === 0 || imageLoading) return;
    const lastImageId = images[images.length - 1]._id;
    setImageUrl(`/images?lastid=${lastImageId}`);
  }, [lastImageId, imageLoading, isPublic]);

  // Optimization. 컴포넌트가 재랜더링 될 때마다 빈번히 호출이 되어, useCallback 을 사용!!

  return (
    <ImageContext.Provider
      value={{
        images: isPublic ? images : myImages,
        setImages: isPublic ? setImages : setMyImages,
        isPublic,
        setIsPublic,
        loaderMoreImages,
        imageLoading,
        imageError,
      }}
    >
      {prop.children}
    </ImageContext.Provider>
  );
};
