import React, { useState } from "react";
import { toast } from "react-toastify";
import axios from "axios";
import CustomInput from "../components/CustomInput";

const CreateImage = () => {
  const [prompt, setPrompt] = useState("");
  const [loadingTime, setLoadingTime] = useState(false);
  const [imgName, setImgName] = useState("Created Image");
  const [imgSrc, setImgSrc] = useState(
    "https://oaidalleapiprodscus.blob.core.windows.net/private/org-8qXRaMQhtoU6Fka8QT1qcZEY/user-QrZA7caSrgbfHxJDTw7ZM7UL/img-ACgQ7qBlU5S4HAot21YH7brh.png?st=2023-06-06T17%3A29%3A57Z&se=2023-06-06T19%3A29%3A57Z&sp=r&sv=2021-08-06&sr=b&rscd=inline&rsct=image/png&skoid=6aaadede-4fb3-4698-a8f6-684d7786b067&sktid=a48cca56-e6da-484e-a814-9c849652bcb3&skt=2023-06-05T23%3A46%3A04Z&ske=2023-06-06T23%3A46%3A04Z&sks=b&skv=2021-08-06&sig=nca8UnIqBfor4WHJqlwgRNwRX42HgT6hMzEWfyzV5Pg%3D"
  );

  const submitHandler = async (e) => {
    try {
      e.preventDefault();
      setLoadingTime(true);
      const result = await axios.post("/create", {
        prompt,
      });
      setImgName(result.data.newPrompt);
      setImgSrc(result.data.url);
      setLoadingTime(false);
      console.log(result.data);
      toast.success("Success!");
    } catch (err) {
      console.error(err);
      toast.error(err.message);
    }
  };

  return (
    <div
      style={{
        maxWidth: "100%",
        marginLeft: "auto",
        marginRight: "auto",
      }}
    >
      <form onSubmit={submitHandler}>
        <CustomInput label="Prompt" setValue={setPrompt} />
        <button
          style={{
            marginTop: 5,
          }}
          type="submit"
        >
          Create Image!
        </button>
      </form>
      {loadingTime && imgName}
      {loadingTime && (
        <img
          src={imgSrc}
          style={{
            maxWidth: "100%",
            marginLeft: "auto",
            marginRight: "auto",
          }}
        />
      )}
    </div>
  );
};

export default CreateImage;
